import React from 'react';

import Add from '../components/Add';
import PrivateRouter from './components/PrivateRouter';

import { authenticAxios } from '../utils/authenticAxios';

class FriendsList extends React.Component {
    state = {
        friends: [],
    };

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        authenticAxios()
            .get('/api/friends')
            .then((res) => {
                // console.log('res.data: ', res.data);
                // console.log('state: ', this.state);
                this.setState({
                    friends: res.data,
                });
            })
            .catch((err) => console.log('error', err));
    };

    render() {
        return (
            <section>
                <PrivateRouter exact path="/add" component={Add} />

                <h1>List of Friends:</h1>
                {this.state.friends.map((value) => {
                    return (
                        <div key={value.id}>
                            <h3>Hey there, I am {value.name}!</h3>
                            <h5>
                                I am {value.age} years old. If ya wanna talk,
                                this is my email: {value.email}
                            </h5>
                        </div>
                    );
                })}
            </section>
        );
    }
}

export default FriendsList;
